document.addEventListener("DOMContentLoaded",(function(){const carousels=document.querySelectorAll(".layout-carousel .swiper");carousels.forEach(carousel=>{new Swiper(carousel,{slidesPerView:1,spaceBetween:20,loop:!0,pagination:{el:".swiper-pagination",clickable:!0},breakpoints:{640:{slidesPerView:1},768:{slidesPerView:2},1024:{slidesPerView:3}}})})}));